fn merge_sort<T: Ord + Copy>(arr: &mut [T]) {
    if arr.len() <= 1 {
        return;
    }
    let mid = arr.len() / 2;
    merge_sort(&mut arr[..mid]);
    merge_sort(&mut arr[mid..]);
    merge(&mut arr[..mid], &mut arr[mid..], arr);
}

fn merge<T: Ord + Copy>(left: &mut [T], right: &mut [T], result: &mut [T]) {
    let mut i = 0;
    let mut j = 0;
    let mut k = 0;
    while i < left.len() && j < right.len() {
        if left[i] <= right[j] {
            result[k] = left[i];
            i += 1;
        } else {
            result[k] = right[j];
            j += 1;
        }
        k += 1;
    }
    while i < left.len() {
        result[k] = left[i];
        i += 1;
        k += 1;
    }
    while j < right.len() {
        result[k] = right[j];
        j += 1;
        k += 1;
    }
}
